// Parse AI response and format as JSON
const aiResponse = $input.first().json.output;

let parsedResponse;
try {
  // Try to parse if AI returned JSON string
  parsedResponse = typeof aiResponse === 'string' ? JSON.parse(aiResponse) : aiResponse;
} catch (e) {
  // Fallback if AI didn't return valid JSON
  const inputText = $('Webhook').first().json.body.text;
  parsedResponse = {
    original_text: inputText,
    translation: "Error en traducci√≥n",
    suggested_answer: aiResponse,
    key_technical_terms: []
  };
}

// Ensure all required fields exist
const response = {
  original_text: parsedResponse.original_text || "",
  translation: parsedResponse.translation || "",
  suggested_answer: parsedResponse.suggested_answer || "",
  key_technical_terms: parsedResponse.key_technical_terms || [],
  timestamp: new Date().toISOString()
};

return { json: response };
